import { useParams } from 'react-router-dom'
import { useEffect, useState } from 'react'

const DetailProduct = (props) => {
    const params = useParams()
    const [products, setProducts] = useState([])
    const [isLoading, setIsLoading] = useState(true)
    const [httpError, setHttpError] = useState()

    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch(`https://front-test-api.herokuapp.com/api/product/${params.productId}`);
            if (!response.ok) {
                throw new Error('Something went wrong!')
            }
            const responseData = await response.json();

            setProducts(responseData)
            setIsLoading(false)
        }
        fetchData()
    }, [params.productId])


    return <div>
        <figure>
            <img src={products.imgUrl} alt={products.model} />
        </figure>
        <div>
            <ul>
                <li>{products.brand}</li>
                <li>{products.model}</li>
                <li>{products.price}</li>
                <li>{products.cpu}</li>
                <li>{products.ram}</li>
                <li>{products.cpu}</li>
                <li>{products.os}</li>
                <li>{products.displayResolution}</li>
                <li>{products.battery}</li>
                <li>
                    <p>Cámara principal</p>
                    <ul>
                        {products.primaryCamera.map(item => <li>{item}</li>)}
                    </ul>
                </li>
                <li>
                    <p>Cámara secundaria</p>
                    <ul>
                        {products.secondaryCmera.map(item => <li>{item}</li>)}
                    </ul>
                </li>
                <li>{products.dimentions}</li>
                <li>{products.weight}</li>
            </ul>
            <form>
                <select type='select' value={products.internalMemory[0]}>
                    {products.internalMemory.map(item => <options>{item}</options>)}
                </select>

                <select type='select' value={products.colors[0]} />
                <button>Añadir</button>
            </form>
        </div>
    </div>
}

export default DetailProduct;
