import { useContext } from 'react'
import '../../styles/Product.css'
import CartContext from '../../store/cart-context'
import AddProducts from './AddProducts'

const Product = (props) => {
    const cartCtx = useContext(CartContext);
    const price = `${props.price?.toFixed(2)}€`
    const addToCartHandler = (amount) => {
        cartCtx.addItem(amount)
    }
    return (
        // TODO añadir key prop
        <li className="feapi-item">
            <figure>
                {/* TODO añadir props */}
                <img src="https://store.storeimages.cdn-apple.com/4668/as-images.apple.com/is/iphone-14-finish-select-202209-6-1inch-blue?wid=5120&hei=2880&fmt=p-jpg&qlt=80&.v=1661026582322" alt="" />
            </figure>
            <h3>{props.name}</h3>
            <span>{price ? price : '€'}</span>
            <AddProducts onAddToCart={addToCartHandler} />
        </li>)
}

export default Product
