import { useParams } from 'react-router-dom'
import { useEffect, useState } from 'react'
import ProductDetail from '../components/Layout/ProductDetail'
const DetailProduct = (props) => {
    let img
    const params = useParams()
    const [products, setProducts] = useState([])
    const [isLoading, setIsLoading] = useState(true)
    const [httpError, setHttpError] = useState()

    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch(`https://front-test-api.herokuapp.com/api/product/${params.productId}`);
            if (!response.ok) {
                throw new Error('Something went wrong!')
            }
            const responseData = await response.json();
            const loadedProduct = []

            loadedProduct.push({
                brand: responseData.brand,
                model: responseData.model,
                price: responseData.price,
                cpu: responseData.cpu,
                ram: responseData.ram,
                os: responseData.os,
                displayResolution: responseData.displayResolution,
                battery: responseData.battery,
                dimentions: responseData.dimentions,
                weight: responseData.weight,
                internalMemory: responseData.internalMemory,
                colors: responseData.colors,
            })

            img = responseData.brand;

            setProducts(loadedProduct)
            setIsLoading(false)
        }
        fetchData()
    }, [params.productId])

    if (isLoading) {
        return <div className="feapi-center">
            <h1>Loading</h1>
            <div className="lds-ellipsis">
                <div></div>
                <div></div>
                <div></div>
                <div></div>
            </div>
        </div>
    }

    if (httpError) {
        return <div className="feapi-center">
            <h1>{httpError}</h1>
        </div>
    }

    const { model } = products[0]
    return <div>
        <figure>
            <img src={img} alt={model} />
        </figure>
        <div>
            <ul>
                <ProductDetail product={products} />
            </ul>
            <form>
                <input type='select' />
                <input type='select' />
                <button>AÃ±adir</button>
            </form>
        </div>
    </div>
}

export default DetailProduct;
