import { useEffect, useState } from "react";
import "../styles/AvailableProducts.css"
import Product from "../components/Products/Product";

const AvailableProducts = (props) => {
    const [products, setProducts] = useState([])
    const [isLoading, setIsLoading] = useState(true)
    const [httpError, setHttpError] = useState()

    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch('https://front-test-api.herokuapp.com/api/product');
            if (!response.ok) {
                throw new Error('Something went wrong!')
            }
            const responseData = await response.json();

            const loadedProduct = [];

            for (const key in responseData) {
                loadedProduct.push({
                    id: responseData[key].id,
                    brand: responseData[key].brand,
                    price: responseData[key].price,
                    model: responseData[key].model,
                    imgUrl: responseData[key].imgUrl
                })
            }
            setProducts(loadedProduct)
            setIsLoading(false)
        }

        fetchData().catch((error) => {
            setIsLoading(false)
            setHttpError(error.message)
        })
    }, [])

    if (isLoading) {
        return <div className="feapi-center">
            <h1>Loading</h1>
            <div className="lds-ellipsis">
                <div></div>
                <div></div>
                <div></div>
                <div></div>
            </div>
        </div>
    }

    if (httpError) {
        return <div className="feapi-center">
            <h1>{httpError}</h1>
        </div>
    }

    return (
        <section className="feapi-section">
            <ul className="feapi-list">
                {products.map(product => {

                    return
                    <Link>
                        <Product key={product.id}
                            id={product.id}
                            brand={product.brand}
                            name={product.model}
                            price={product.price}
                            imgUrl={product.imgUrl} />
                    </Link>
                })}
            </ul>
        </section>
    )
}

export default AvailableProducts;
