import { useContext } from "react";
import CartContext from '../../store/cart-context'
import "../../styles/HeaderCartBtn.css";

const HeaderCartBtn = (props) => {
    const cartCtx = useContext(CartContext);
    const numOfCartItems = cartCtx.item.reduce((currentNumber, item) => {
        return currentNumber + item.amount;
    }, 0);
    return (<button className="feapi-cart__button" onClick={props.onClick}>
        <span className="feapi-cart__icon">
            <i className="bi bi-cart"></i>
        </span>
        <span>{props.text}</span>
        <span className="feapi-cart__badge">{numOfCartItems}</span>
    </button>)
}

export default HeaderCartBtn;
