import { useParams } from 'react-router-dom'
import { useEffect, useState } from 'react'
import ProductDetail from '../components/Layout/ProductDetail'
import CartContext from '../store/cart-context'
const DetailProduct = (props) => {
    const cartCtx = useContext(CartContext);
    const params = useParams()
    const [img, setImg] = useState()
    const [products, setProducts] = useState([])
    const [isLoading, setIsLoading] = useState(true)
    const [httpError, setHttpError] = useState()
    const addToCartHandler = (amount) => {
        cartCtx.addItem({
            id: props.id,
            name: props.name,
            amount: amount,
            price: props.price
        })
    }

    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch(`https://front-test-api.herokuapp.com/api/product/${params.productId}`);
            if (!response.ok) {
                throw new Error('Something went wrong!')
            }
            const responseData = await response.json();
            const loadedProduct = []

            loadedProduct.push({
                brand: responseData.brand,
                model: responseData.model,
                price: responseData.price,
                cpu: responseData.cpu,
                ram: responseData.ram,
                os: responseData.os,
                displayResolution: responseData.displayResolution,
                battery: responseData.battery,
                dimentions: responseData.dimentions,
                weight: responseData.weight,
                internalMemory: responseData.internalMemory,
                colors: responseData.colors,
            })


            setImg(responseData.imgUrl)
            setProducts(loadedProduct)
            setIsLoading(false)
        }
        fetchData().catch((error) => {
            setIsLoading(false)
            setHttpError(error.message)
        })
    }, [params.productId])

    if (isLoading) {
        return <div className="feapi-center">
            <h1>Loading</h1>
            <div className="lds-ellipsis">
                <div></div>
                <div></div>
                <div></div>
                <div></div>
            </div>
        </div>
    }

    if (httpError) {
        return <div className="feapi-center">
            <h1>{httpError}</h1>
        </div>
    }

    const { model } = products[0]
    return <div className='feapi-detail__img'>
        <figure>
            <img src={img} alt={model} />
        </figure>
        <div className='feapi-detail__info'>
            <ul>
                <ProductDetail product={products} />
            </ul>
            <form className='feapi-detail__from' >
                <AddProducts onAddToCart={addToCartHandler} />
            </form>
        </div>
    </div>
}

export default DetailProduct;
